import React from 'react'
import { useEffect, useState } from 'react';
import axios from 'axios'


const baseURL='https://api.coingecko.com/api/v3/search/trending'

function TrendingCoin() {
   
    const [trending, setTrending]=useState(null);
    const [error, setError]=useState(null);



    useEffect(()=>{

        axios.get(baseURL).then((resp)=>{
           setTrending(resp.data);
        }).catch(error=>{
            setError(error);
        });

    }, []);

    console.log(trending);

    if(error) return `Error:${error.message}`

    


  return (
    <div className='mt-8 grid grid-cols-4 gap-4'> 

       {trending && trending.coins.map(coin=>

          <div className='card' key={coin.item.coin_id}>
              <img className='h-fit w-100' src={coin.item.thumb}></img>
              <span>{coin.item.name}</span>
              <span>{coin.item.market_cap_rank}</span>
              <span> {coin.item.price_btc}</span>
            
          </div>
        
        
        )}


    </div>
  )
}

export default TrendingCoin
