import { Box, Container, RadioGroup, Radio, HStack, VStack, Text, Image, Stat, StatLabel, StatNumber, StatHelpText, StatArrow } from '@chakra-ui/react'
import React, { useState, useEffect } from 'react'
import Loader from './Loader';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { baseURL } from '../index';
import Error from './Error';

function CoinDetails() {

    const [coin, setCoin]=useState([]);
    const [loading, setloading]=useState(true);
    const [error, setError]=useState(false);
    const [currency, setCurrency]=useState("inr");
    const params=useParams();

    const currencySymbol = currency==="inr"?"₹":currency==="eur"?"€": "$"


    
    useEffect(()=>{
       
        const fetchCoin = async ()=>{

            try{
              const {data}=await axios.get(`${baseURL}/coins/${params.id}`);
              setCoin(data);
              setloading(false);
            }
            catch(error){
                setloading(false);
                setError(true);

            }


        }

        fetchCoin();

     }, [])

       
    if(error) return <Error message={"Unable to Fetching Coin..."}/>


  return (
     <Container maxW={'container.xl'}>
       
       {loading? <Loader/> : <>
            
            <Box width={"full"} borderWidth={1}>
                lakjd
            </Box>
               
           <RadioGroup value={currency} onChange={setCurrency} p={"8"}>
              <HStack>
                <Radio value={"inr"}>₹ INR</Radio>        
                <Radio value={"usd"}>$ USD</Radio>  
                <Radio value={"eur"}>€ EUR</Radio>

              </HStack>
           </RadioGroup>


           <VStack spacing={"4"} p={"16"}>

            <Text fontSize={"small"} alignSelf={"center"}>
                Last Updated on {Date(coin.market_data_last_updated).split("G")[0]}
            </Text>


            <Image src={coin.image.large} 
                    w={"16"}
                    h={"16"}
                    objectFit={"contain"}
              
            />

            <Stat>
                <StatLabel>{coin.name}</StatLabel>
                <StatNumber>
                    {currencySymbol}
                    {coin.market_data.current_price[currency]}
                </StatNumber>

                <StatHelpText>
                    <StatArrow type='decrease'>
                        {coin.market_data.price_change_}
                    </StatArrow>
                </StatHelpText>

            </Stat>

           </VStack>



       
       </>}

     </Container>
  )
}

export default CoinDetails
